package wtn;
import java.util.*;

public class Q16 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n,i,j;
	    Scanner sc=new Scanner(System.in);
	    n=sc.nextInt();
	    for(i=1;i<=n;i++)
	    {
	    	for(j=1;j<=i;j++)
	    	{
	    		System.out.print("*");
	              
	        }
	    	System.out.println();
	    }
	}
}
